import React, { useState } from "react";

function App() {
  const [file, setFile] = useState(null);
  const [dataToHide, setDataToHide] = useState("");
  const [response, setResponse] = useState(null);

  const handleFileChange = (e) => setFile(e.target.files[0]);
  const handleDataChange = (e) => setDataToHide(e.target.value);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!file) {
      alert("Please select an image file.");
      return;
    }
    if (!dataToHide) {
      alert("Please enter data to hide.");
      return;
    }

    const formData = new FormData();
    formData.append("file", file);
    formData.append("data", dataToHide);

    try {
      const res = await fetch("http://localhost:8000/uploadfile", {
        method: "POST",
        body: formData,
      });

      const result = await res.json();
      setResponse(result);
    } catch (err) {
      setResponse({ error: err.message });
    }
  };

  return (
    <div style={{ padding: 20 }}>
      <h2>Hide Data in Image</h2>
      <form onSubmit={handleSubmit}>
        <input type="file" accept="image/*" onChange={handleFileChange} /><br /><br />
        <input
          type="text"
          placeholder="Enter data to hide"
          value={dataToHide}
          onChange={handleDataChange}
          style={{ width: "300px" }}
        /><br /><br />
        <button type="submit">Upload & Hide</button>
      </form>

      {response && (
        <pre style={{ marginTop: 20, background: "#eee", padding: 10 }}>
          {JSON.stringify(response, null, 2)}
        </pre>
      )}
    </div>
  );
}

export default App;
