import React, { useState } from "react";

function App() {
  // State for file to hide data in
  const [fileToHide, setFileToHide] = useState(null);
  const [hideResponse, setHideResponse] = useState(null);

  // State for file to extract data from
  const [fileToExtract, setFileToExtract] = useState(null);
  const [extractResponse, setExtractResponse] = useState(null);

  // Handle file selection for hiding data
  const handleHideFileChange = (e) => {
    setFileToHide(e.target.files[0]);
    setHideResponse(null);
  };

  // Handle file selection for extracting data
  const handleExtractFileChange = (e) => {
    setFileToExtract(e.target.files[0]);
    setExtractResponse(null);
  };

  // Submit file to backend to hide data
  const handleHideSubmit = async (e) => {
    e.preventDefault();
    if (!fileToHide) return alert("Choose a file to hide data in");

    const formData = new FormData();
    formData.append("file", fileToHide);

    try {
      const res = await fetch("http://localhost:8000/uploadfile", {
        method: "POST",
        body: formData,
      });
      const data = await res.json();
      setHideResponse(data);
    } catch (err) {
      setHideResponse({ error: err.message });
    }
  };

  // Submit file to backend to extract data
  const handleExtractSubmit = async (e) => {
    e.preventDefault();
    if (!fileToExtract) return alert("Choose a file to extract data from");

    const formData = new FormData();
    formData.append("file", fileToExtract);

    try {
      const res = await fetch("http://localhost:8000/extractfile", {
        method: "POST",
        body: formData,
      });
      const data = await res.json();
      setExtractResponse(data);
    } catch (err) {
      setExtractResponse({ error: err.message });
    }
  };

  return (
    <div style={{ maxWidth: 600, margin: "auto", padding: 20, fontFamily: "Arial" }}>
      <h1>Image Steganography</h1>

      {/* Hide Data Section */}
      <section style={{ marginBottom: 40 }}>
        <h2>Hide Data in Image</h2>
        <form onSubmit={handleHideSubmit}>
          <input type="file" accept="image/*" onChange={handleHideFileChange} />
          <button type="submit" style={{ marginLeft: 10 }}>Upload & Hide</button>
        </form>
        {hideResponse && (
          <pre style={{ background: "#eee", padding: 10, marginTop: 10 }}>
            {JSON.stringify(hideResponse, null, 2)}
          </pre>
        )}
      </section>

      {/* Extract Data Section */}
      <section>
        <h2>Extract Data from Image</h2>
        <form onSubmit={handleExtractSubmit}>
          <input type="file" accept="image/*" onChange={handleExtractFileChange} />
          <button type="submit" style={{ marginLeft: 10 }}>Upload & Extract</button>
        </form>
        {extractResponse && (
          <pre style={{ background: "#eee", padding: 10, marginTop: 10 }}>
            {JSON.stringify(extractResponse, null, 2)}
          </pre>
        )}
      </section>
    </div>
  );
}

export default App;
