import React, { useState } from "react";

function App() {
  const [file, setFile] = useState(null);
  const [secret, setSecret] = useState("");
  const [resultImage, setResultImage] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!file || !secret) {
      alert("Please select a file and enter secret text.");
      return;
    }

    const formData = new FormData();
    formData.append("file", file);
    formData.append("secret", secret);

    try {
      const response = await fetch("http://localhost:8000/hide", {
        method: "POST",
        body: formData,
      });

      if (!response.ok) {
        alert("Error in hiding text");
        return;
      }

      const blob = await response.blob();
      setResultImage(URL.createObjectURL(blob));
    } catch (error) {
      alert("Error: " + error.message);
    }
  };

  return (
    <div style={{ padding: 20 }}>
      <h1>Steganography Image Hider</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="file"
          accept="image/*"
          onChange={(e) => setFile(e.target.files[0])}
        />
        <br /><br />
        <input
          type="text"
          placeholder="Enter secret text"
          value={secret}
          onChange={(e) => setSecret(e.target.value)}
        />
        <br /><br />
        <button type="submit">Hide Text</button>
      </form>

      {resultImage && (
        <div style={{ marginTop: 20 }}>
          <h2>Image with Hidden Text:</h2>
          <img src={resultImage} alt="Stego result" style={{ maxWidth: "400px" }} />
          <br />
          <a href={resultImage} download="stego_image.png">Download Image</a>
        </div>
      )}
    </div>
  );
}

export default App;
